mapper.update:
update Members set member_totPoint = member_totPoint +
if( -- 유저의 등급에 따른 포인트배수 구하기 (select V.grade_benefit from(select Grades.grade_benefit from Grades Grades join Members M on M.grade_name = Grades.grade_name where M.member_id = #{userName}) V) =10,
(select CAST((select (select bucket.bucket_totBuyPrice from Buckets bucket JOIN Payments payment on payment.buyProduct_no = bucket.bucket_no where payment.member_id = #{userName})) * 0.1 AS signed integer)),
if( (select B.grade_benefit from(select Grades.grade_benefit from Grades Grades join Members M on M.grade_name = Grades.grade_name where M.member_id = #{userName}) B) = 5,
(select CAST((select (select bucket.bucket_totBuyPrice from Buckets bucket JOIN Payments payment on payment.buyProduct_no = bucket.bucket_no where payment.member_id = #{userName})) * 0.05 AS signed integer)) ,0)) ,
member_payment = member_payment+ (select bucket.bucket_totBuyPrice from Buckets bucket JOIN Payments payment on payment.buyProduct_no = bucket.bucket_no where payment.member_id = #{userName})
where member_id = #{userName}";

update Members set
grade_name = 
if(
	((select M.member_payment from(select member_payment from Members where member_id = #{userName})M)>= (SELECT grade_highExpendi FROM Grades where grade_name = "A") ) or
	((select M.member_payment from(select member_payment from Members where member_id = #{userName})M)  >= (SELECT grade_lowExpendi FROM Grades where grade_name = "A")),"A",
    if(
		((select M.member_payment from(select member_payment from Members where member_id = #{userName})M)>= (SELECT grade_highExpendi FROM Grades where grade_name = "B") ) or
		((select M.member_payment from(select member_payment from Members where member_id = #{userName})M)  >= (SELECT grade_lowExpendi FROM Grades where grade_name = "B")),"B","C"
    )
)
where member_id = #{userName};
