mapper.select:
select * from goot6.Boards where member_id = #{member_id}
and board_isDelete = 'N' order by board_writeDate desc limit
#{pageStart}, #{perPageNum}

select r.member_id, r.board_no, b.board_category,
r.replyBoard_content, r.replyBoard_writeDate, r.replyBoard_isdelete
from Boards b, ReplyBoards r where b.board_no = r.board_no and
r.member_id = #{member_id}

	and b.board_category = #{board_category}

order by r.replyBoard_writeDate desc limit #{pageStart}, #{perPageNum}

select count(*) from Boards b, ReplyBoards r where b.board_no =
r.board_no and r.member_id = #{member_id}

	and b.board_category = #{board_category}
