mapper.update:
update Buckets 
set bucket_buyQty = #{bucket_buyQty}, bucket_totBuyPrice = #{bucket_totBuyPrice}
where member_id = #{member_id} and bucket_no = #{bucket_no}

mapper.select:
  	select prod.product_id, maincg.mainCategory_content, middlecg.middleCategory_content, prod.product_name, prod.product_sellPrice, prod.product_img1, prod.product_prodAvgScore
	from MainCategories maincg, MiddleCategories middlecg, Products prod where maincg.mainCategory_id = middlecg.mainCategory_id and prod.mainCategory_id = maincg.mainCategory_id and prod.middleCategory_id = middlecg.middleCategory_id 
	and maincg.mainCategory_id = #{mainCategory_id} and middlecg.middleCategory_id = #{middleCategory_id}


		and prod.product_sellPrice <= 50000


		and prod.product_sellPrice >= #{minPrice} and prod.product_sellPrice <= #{maxPrice}


		and prod.product_sellPrice >= 1000000



		and prod.product_prodAvgScore = #{prodScore}


	group by product_id


		order by prod.product_prodAvgScore desc, prod.product_saleQty desc


		order by prod.product_sellPrice asc


		order by prod.product_sellPrice desc


		order by prod.product_date desc


	 limit #{pageStart}, #{perPageNum}

  	select count(*)
	from MainCategories maincg, MiddleCategories middlecg, Products prod where maincg.mainCategory_id = middlecg.mainCategory_id and prod.mainCategory_id = maincg.mainCategory_id and prod.middleCategory_id = middlecg.middleCategory_id 
	and maincg.mainCategory_id = #{mainCategory_id} and middlecg.middleCategory_id = #{middleCategory_id}


		and prod.product_sellPrice <= 50000


		and prod.product_sellPrice >= #{minPrice} and prod.product_sellPrice <= #{maxPrice}


		and prod.product_sellPrice >= 1000000



		and prod.product_prodAvgScore = #{prodScore}

select count(*) from MainCategories maincg, MiddleCategories middlecg, Products prod where maincg.mainCategory_id = middlecg.mainCategory_id and prod.mainCategory_id = maincg.mainCategory_id and prod.middleCategory_id = middlecg.middleCategory_id
and prod.product_sellPrice <= 10000
and prod.product_sellPrice >= #{minPrice} and prod.product_sellPrice <= #{maxPrice}
and prod.product_sellPrice >= 100000
and prod.product_prodAvgScore >= #{prodScore}
and prod.product_name like concat('%',#{keyword},'%')
